// Helper function to search stations
export const searchStations = (query) => {
    if (!query || query.length < 2) return [];
    const q = query.toLowerCase();

    // First check station codes map
    const results = [];
    for (const [code, name] of Object.entries(stationCodes)) {
        if (code.toLowerCase().includes(q) || name.toLowerCase().includes(q)) {
            results.push({ code, name });
        }
        if (results.length >= 20) break; // Limit results
    }

    return results;
};
